import React, { useState } from "react";
import "./Navbar.css";
import { assets } from "../../assets/assets";
import { CgProfile } from "react-icons/cg";
import { TbReportMedical } from "react-icons/tb";
import { IoIosLogOut } from "react-icons/io";

const Navbar = ({ setShowLogin }) => {
  const [menu, setMenu] = useState("home");
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Toggle mobile menu visibility
  const handleMenuToggle = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <nav className="navbar">
      <img
        src={assets.logo}
        style={{ width: "110px", height: "100px" }}
        alt=""
      />

      {/* Mobile Menu Icon */}
      <div className="mobile-menu-icon" onClick={handleMenuToggle}>
        &#9776; {/* This is the hamburger icon */}
      </div>

      {/* Navbar Menu */}
      <ul className={`navbar-menu ${isMobileMenuOpen ? "active" : ""}`}>
        <li
          onClick={() => setMenu("home")}
          className={menu === "home" ? "active" : ""}
        >
          Home
        </li>
        <li
          onClick={() => setMenu("tournament")}
          className={menu === "tournament" ? "active" : ""}
        >
          Tournaments
        </li>
        <li
          onClick={() => setMenu("contact")}
          className={menu === "contact" ? "active" : ""}
        >
          Contact Us
        </li>
      </ul>

      {/* Navbar right-side section */}
      {/* <div className="navbar-right">
        <button onClick={() => setShowLogin(true)}>SignIn</button>
      </div> */}

      <div className="navbar-right">
        {<p>hii</p> ? (
          <button onClick={() => setShowLogin(true)}>Sign In</button>
        ) : (
          <div className="navbar-profile">
            <CgProfile style={{ height: "30px", width: "30px" }} />
            <ul className="nav-profile-dropdown">
              <li>
                <TbReportMedical style={{ height: "20px", width: "20px" }} />
                <p /*</li>onClick={() => navigate("/myorders")}*/>
                  Appointment
                </p>
              </li>
              <hr />
              <li onClick={logOut}>
                <IoIosLogOut style={{ height: "20px", width: "20px" }} />
                <p>Logout</p>
              </li>
            </ul>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
