import React from "react";
import "./GameCard.css";
import { assets } from "../../assets/assets";

// Data array with details for each game card
const gameDetails = [
  {
    image: assets.card1,
    category: "Champion's Arena",
    title: "The best online game is out now!",
    description:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Quis ipsum suspendisse ultricies gravida...",
  },
  {
    image: assets.card2,
    category: "Crisis Control",
    title: "New adventure game released!",
    description:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Quis ipsum suspendisse ultricies gravida...",
  },
  {
    image: assets.card3,
    category: "Survivor’s Arena",
    title: "Epic game update is live!",
    description:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Quis ipsum suspendisse ultricies gravida...",
  },
];

const GameCard = () => {
  return (
    <div className="container">
      {/* Mapping through the gameDetails array */}
      {gameDetails.map((game, index) => (
        <div className="game-card" key={index}>
          <div className="game-image">
            <img src={game.image} alt={`Game ${index + 1}`} />
          </div>
          <div className="game-info">
            <span className="date-category">
              <span className="category">{game.category}</span>
            </span>

            <div className="read-more">READ MORE »</div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default GameCard;
