const gameDetails = [
  {
    image: assets.card1,
    category: "Champion's Arena",
  },
  {
    image: assets.card2,
    category: "Crisis Control",
  },
  {
    image: assets.card3,
    category: "Survivor’s Arena",
  },
];

const GameCard = () => {
