import React from "react";
import "./GameCard.css";
import { assets } from "../../assets/assets";
import { useNavigate } from "react-router-dom";

// Data array with details for each game card
const gameDetails = [
  {
    image: assets.card1,
    category: "Champion's Arena",
  },
  {
    image: assets.card2,
    category: "Crisis Control",
  },
  {
    image: assets.card3,
    category: "Survivor’s Arena",
  },
];

const GameCard = ({ onSelectTournament }) => {
  const navigate = useNavigate();

  // Handle when a tournament is selected (either navigate or call the prop function)
  const handletournament = (category) => {
    if (onSelectTournament) {
      onSelectTournament(category); // Call the prop function if provided
    } else {
      navigate("/tournament", { state: { tournamentName: category } });
    }
  };

  return (
    <div className="container">
      {/* Mapping through the gameDetails array */}
      {gameDetails.map((game, index) => (
        <div className="game-card" key={index}>
          <div className="game-image">
            <img src={game.image} alt={`Game ${index + 1}`} />
          </div>
          <div className="game-info">
            <span className="date-category">
              <span className="category">{game.category}</span>
            </span>
            <button
              onClick={() => handletournament(game.category)}
              className="read-more"
            >
              Register »
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default GameCard;
