import React from "react";
import "./Info.css";

// Data array with details for each tournament
const tournamentDetails = [
  {
    entryFee: "₹50",
    title: "Champion's Arena",
    description:
      "This is the content for the first section. It has some text explaining the topic in detail.",
  },
  {
    entryFee: "₹30",
    title: "Crisis Control",
    description:
      "This is the content for the second section. It elaborates on the topic further with additional information.",
  },
  {
    entryFee: "₹20",
    title: "Survivor’s Arena",
    description:
      "This is the content for the third section. It concludes the topic with final thoughts.",
  },
];

const Info = () => {
  return (
    <div className="grid-container">
      {/* Mapping through the tournamentDetails array */}
      {tournamentDetails.map((tournament, index) => (
        <div className="grid-item" key={index}>
          <h2>
            <span>Entry fee {tournament.entryFee}</span>
          </h2>
          <h1>{tournament.title}</h1>
          <p>{tournament.description}</p>
          {/* Optional button for additional interaction */}
          {index === 2 && <button className="read-more-btn">Read More</button>}
        </div>
      ))}
    </div>
  );
};

export default Info;
